//Library Imports
import React, { useReducer, useState, useRef } from "react";
import { render } from "react-dom";
import { Stage, Layer, Rect, Image } from "react-konva";


//Component Imports
import ImageWrapper from "./ImageWrapper";

//Reducer Imports
import { reducer } from "../reducers/letterReducer";

//Helper Methods
import { addImage,handleStageClick } from '../helperMethods';

//Static Resources
import smiley from "../assets/smiley.png";
import groot from '../assets/groot.svg'
import goodVibes from '../assets/goodVibes.svg'
import cuteCat from '../assets/cuteCat.svg'
import bears from '../assets/barebears.png'
import alien from '../assets/alien.png'

//Hook Imports
import useWindowSize from '../hooks/useWindowSize';

const Letter = () => {
  //Reducer State
  const [items, dispatch] = useReducer(reducer, []);

  //React Refs
  const stageEl = React.createRef();
  const layerEl = React.createRef();
  const containerRef = React.createRef();

  //Local State
  const [selectedId, selectShape] = useState(null);
  const [height, width] = useWindowSize();

  //ComponentDidMount methods
  React.useEffect((
    () => {
    console.log(height,width)    
    }
  ),[width])

  React.useEffect((
    () => {
    console.log(height,width)    
    }
  ),[height])

  return (
    <div style = {{height:"60vh"}}>
      <button onClick={() => addImage(smiley,dispatch,selectShape)}>Add Image</button>
      <Stage
        ref={stageEl}
        width={0.6*width}
        height={0.5*height}
        style ={{border:"1px solid black"}}
        onMouseDown={(e) => handleStageClick(e,selectShape)}
      >
        <Layer>
          {items.map((item, itemIndex) => {
              switch (item.type) {
                  case 'IMAGE': {
                      return (
                        <ImageWrapper
                        item={item}
                        key={itemIndex}
                        selectShape={selectShape}
                        selected={item.id === selectedId}
                        itemIndex={itemIndex}
                        dispatch={dispatch}
                      />
                      )
                  }
              }

          })}
        </Layer>
      </Stage>
      </div>
  );
};

export default Letter;
