import React from 'react';
import Rodal from 'rodal';

// include styles
import 'rodal/lib/rodal.css';

class RodalModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = { visible: true };
  }

  show() {
    this.setState({ visible: true });
  }

  hide() {
    this.setState({ visible: false });
  }

  render() {
    return (
      <div>
        <button onClick={this.show.bind(this)}>Login</button>

        <Rodal height="100vh" width="100vw" showCloseButton={true} visible={this.state.visible} onClose={this.hide.bind(this)}>
          <div className="flex justify-center items-center">
            <div className="max-w-md">
              <div className = "flex flex-col justify-center items-center h-screen">
              <p className="text-4xl text-bold text-center">Sign-Up</p>
              <p className="text-2xl text-bold">Already a member? Log in</p>
                <button style={{width:"250px"}}class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-6 px-4 rounded">
                  Sign Up With Facebook
              </button>
              <button style={{width:"40vw", maxWidth:"350px"}} class="bg-blue-500 hover:bg-blue-700 mt-4 text-white font-bold py-6 px-4 rounded">
                Sign Up With Google
              </button>
              </div>

            </div>

          </div>

            </Rodal>
      </div>
    );
  }
}

export default RodalModal;
