//Library Imports
import React, { useReducer, useState, useRef } from "react";
import { render } from "react-dom";
import { Stage, Layer, Rect, Image } from "react-konva";
import { v4 as uuidv4 } from "uuid";

//Component Imports
import ImageWrapper from "./ImageWrapper";

//Reducer Imports
import { ADD_IMAGE } from '../actionTypes';
import { reducer } from "../reducers/letterReducer";

//Static Resources
import smiley from "../assets/smiley.png";
import groot from '../assets/groot.svg'
import goodVibes from '../assets/goodVibes.svg'
import cuteCat from '../assets/cuteCat.svg'
import bears from '../assets/barebears.png'
import alien from '../assets/alien.png'

const Letter = () => {
  const [items, dispatch] = useReducer(reducer, []);
  const stageEl = React.createRef();
  const layerEl = React.createRef();




  //Local State
  const [selectedId, selectShape] = useState(null);
  const [, updateState] = React.useState();
  const [editing, setEditing] = React.useState(false);

  const addImage = (image) => {
    const id = uuidv4();

    dispatch({
      type: ADD_IMAGE,
      payload: { image, id, width: 50, height: 50, x: 100, y: 100 }
    });
    selectShape(id);
  };

  console.log(selectedId);

  return (
    <div style = {{height:"60vh"}}>
      <button onClick={() => addImage(smiley)}>Add Image</button>
      <Stage
        ref={stageEl}

        className="border"
        onMouseDown={(e) => {
          // deselect when clicked on empty area
          const clickedOnEmpty = e.target === e.target.getStage();
          if (clickedOnEmpty) {
            selectShape(null);
          }
        }}
      >
        <Layer>
          {items.map((item, itemIndex) => {
              switch (item.type) {
                  case 'IMAGE': {
                      return (
                        <ImageWrapper
                        item={item}
                        key={itemIndex}
                        selectShape={selectShape}
                        selected={item.id === selectedId}
                        itemIndex={itemIndex}
                        dispatch={dispatch}
                      />
                      )
                  }
              }

          })}
        </Layer>
      </Stage>
      </div>
  );
};

export default Letter;
