import React from 'react';
import { Text, Transformer } from 'react-konva';

const TextWrapper = ({ item, selectShape, selected, dispatch, itemIndex,text }) => {
    //Item Properties
    const { image, x, y, width, height,value } = item
    const shapeRef = React.useRef()
    const trRef = React.useRef()

    React.useEffect(() => {
        if (selected) {
        // we need to attach transformer manually
        trRef.current.setNode(shapeRef.current)
        trRef.current.getLayer().batchDraw()
        }
    }, [selected])

    return (
        <>
            <Text
                x={x}
                y={y}
                width={width}
                height={height}
                text={value}
                ref={shapeRef}
                onClick={() => selectShape(item.id)}
                onDragEnd={(e) => {
                    dispatch({
                      type: 'UPDATE_IMAGE_POSITION',
                      payload: { x: e.target.x(), y: e.target.y(), itemIndex }
                    })
                  }}
                draggable
            />
            {selected && <Transformer ref={trRef} />} 
        </>
    )
}

export default TextWrapper;
