import React from 'react'

const InfographicItem = ({header,text,image,index}) => {
    return (
        <div className="flex-col flex items-start text-left" style={{ width: "80vw" }}>
            <img src = {image} style = {{width:"40vw",paddingBottom:"20px"}} />
            <div className = "flex-col items-start justify-start">
                <p className="text-2xl">{index+1}. {header}</p>
                <p className="text-sm">{text}</p>
            </div>

        </div>
    )
}

export default InfographicItem
