import React from 'react'

//Static Resources
import smiley from "../assets/smiley.png";
import groot from '../assets/groot.svg'
import goodVibes from '../assets/goodVibes.svg'
import cuteCat from '../assets/cuteCat.svg'
import bears from '../assets/barebears.png'
import alien from '../assets/alien.png'

//Icon Imports
import Image from '../assets/icons/image.svg';
import Text from '../assets/icons/text.svg';
import Brush from '../assets/icons/brush.svg'

//Defined Action Types
import { LINE_SELECTION, TEXT_SELECTION, IMAGE_SELECTION } from '../actionTypes';

//Helper Methods
import { addImage } from '../helperMethods';


const Toolbar = ({ dispatch, selectShape }) => {
    const [currentScreen,setCurrentScreen] = React.useState(null)

    return (
        <div className = "flex flex-row items-center justify-center bg-red-400">
            <div className = "flex flex-row justify-between py-4" >
                <img style={{ width: "10vw", maxWidth: "40px" }} src={Image} />
                <img style={{ width: "10vw", maxWidth: "40px" }} src={Text} />
                <img style={{ width: "10vw", maxWidth: "40px" }} src={Brush} />
            </div>
        </div>


    )
}

export default Toolbar
